import{_ as o}from"./plugin-vue_export-helper-x3n3nnut.js";import{o as r,c as l,d as e}from"./app-_66NPXUO.js";const n={},t=e('<h3 id="mysql日志-常见的日志有哪些及作用" tabindex="-1"><a class="header-anchor" href="#mysql日志-常见的日志有哪些及作用" aria-hidden="true">#</a> Mysql日志：常见的日志有哪些及作用</h3><h3 id="_1-常见的日志类型分类-【针对于innodb存储引擎】" tabindex="-1"><a class="header-anchor" href="#_1-常见的日志类型分类-【针对于innodb存储引擎】" aria-hidden="true">#</a> 1. 常见的日志类型分类：【针对于InnoDB存储引擎】</h3><ul><li><strong>错误日志（error log）</strong>：记录了Mysql的启动、运行、关闭过程</li><li><strong>二进制日志（binary log）</strong>：主要记录更改数据库数据的SQL语句</li><li><strong>一般查询日志（general query log）</strong>：已建立连接的客户端发送给Mysql服务器的所有SQL记录，SQL量比较大，默认关闭，不建议开启</li><li><strong>慢查询日志（slow query log）</strong>：执行时间超过<code>long_query_time</code>秒钟的查询，排查解决SQL慢查询问题有用</li><li><strong>事务日志（redo log和undo log）</strong>：redo log是重做日志，undo log是回滚日志</li><li><strong>中续日志（redo log）</strong>：elay log 是复制过程中产生的日志，很多方面都跟 binary log 差不多。不过，relay log 针对的是主从复制中的从库。</li><li><strong>DDL 日志(metadata log)</strong> ：DDL 语句执行的元数据操作。</li></ul>',3),s=[t];function i(a,g){return r(),l("div",null,s)}const c=o(n,[["render",i],["__file","MySQL常见日志及其作用.html.vue"]]);export{c as default};
